          const email = usersArray[i].email;
          const photo = usersArray[i].picture


            tableItems.push(
            <tr key={i}>
              <th>{i}</th>
              <th>{name}</th>
              <th>{location}</th>
              <th>{gender}</th>
              <th>{age}</th>
              <th>{email}</th>
              <th><img src={photo} class="photo" alt="profile" /></th>
                </tr>)
        }


        console.log(tableItems);

        return (<div>
         <Table striped bordered hover>
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Location</th>
            <th>Gender</th>
            <th>Age</th>
            <th>Email</th>
            <th>Headshot</th>
            </tr>
        </thead>
        <tbody>
            {tableItems}           
        </tbody>
        </Table>
        </div>)
    } 


